package com.bishant.restaurantfinder.activity;import android.content.Intent;import android.os.Bundle;import android.os.Handler;import android.view.Window;import android.view.WindowManager;import com.bishant.restaurantfinder.R;import com.bishant.restaurantfinder.helper.RestaurantActivity;import com.bishant.restaurantfinder.helper.TypyingAnimation;import com.bishant.restaurantfinder.utilities.Utilities;public class SplashScreenActivity extends RestaurantActivity {    private TypyingAnimation writer;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        requestWindowFeature(Window.FEATURE_NO_TITLE);        getWindow().setFlags(WindowManager.LayoutParams.FLAG_FULLSCREEN,                WindowManager.LayoutParams.FLAG_FULLSCREEN);        setContentView(R.layout.activity_splash_screen);        writer = findViewById(R.id.app_name);        writer.setCharacterDelay(50);        writer.animateText(getResources().getString(R.string.app_name));        if (Utilities.isFirstRun()) {            if (Utilities.isLogin()) {                new Handler().postDelayed(new Runnable() {                    @Override                    public void run() {                        Intent intent = new Intent(SplashScreenActivity.this, MainActivity.class);                        startActivity(intent);                        finish();                    }                }, 1000);            } else {                new Handler().postDelayed(new Runnable() {                    @Override                    public void run() {                        Intent intent = new Intent(SplashScreenActivity.this, LoginActivity.class);                        startActivity(intent);                        finish();                    }                }, 1000);            }        } else {            new Handler().postDelayed(new Runnable() {                @Override                public void run() {                    Intent intent = new Intent(SplashScreenActivity.this, BoardingActivity.class);                    startActivity(intent);                    finish();                }            }, 1000);        }    }    @Override    protected void initialiseViews() {    }    @Override    protected void initialiseListeners() {    }}